@loader.tds
class FuckerMod(loader.Module):
    strings = {
        "name": "–£–Ω–∏–∂–∞—Ç–æ—Ä Premium"
    }
    
    async def client_ready(self, client, db):
        self.db = db
        self.users = self.db.get("fucker", "users", [])
        self.phrases = self.db.get("fucker", "phrases", [])
    
    def add_phrase(self, phrase: str):
        self.phrases.append(phrase)
        self.db.set("fucker", "phrases", self.phrases)
    
    def add_user(self, user_id: int):
        self.users.append(user_id)
        self.db.set("fucker", "users", self.users)
    
    def remove_user(self, user_id: int):
        self.users.remove(user_id)
        self.db.set("fucker", "users", self.users)
    
    async def clearbcmd(self, message):
        """–ù–∏–∫–æ–≥–æ –Ω–µ —Ç—Ä–∞—Ö–∞—Ç—å"""
        
        self.users = []
        self.db.set("fucker", "users", self.users)
        
        await utils.answer(
            message=message,
            response="<b>–ë–æ–ª—å—à–µ —è –Ω–∏–∫–æ–≥–æ –Ω–µ —É–Ω–∏–∂–∞—é</b>"
        )
    
    async def baddcmd(self, message):
        """–î–æ–±–∞–≤–∏—Ç—å —Ñ—Ä–∞–∑—É | .bulla <—Ñ—Ä–∞–∑–∞>"""
        
        args = utils.get_args_raw(message)
        
        if not args:
            return await utils.answer(
                message=message,
                response="<b>üö´ –ù–µ —É–∫–∞–∑–∞–Ω –∞—Ä–≥—É–º–µ–Ω—Ç</b>"
            )
        
        self.add_phrase(args)
        
        await utils.answer(
            message=message,
            response="<b>–§—Ä–∞–∑–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∞</b>"
        )
    
    async def bullrcmd(self, message):
        """–í–∫–∏–Ω—É—Ç—å —Ä–∞–Ω–¥–æ–º–Ω–æ–µ –æ—Å–∫–æ—Ä–±–ª–µ–Ω–∏–µ"""
        
        await utils.answer(
            message=message,
            response=random.choice(bullr + self.phrases)
        )
    
    async def addbcmd(self, message):
        """–ë—É–ª–ª–∏—Ç—å —á–µ–ª–∞. <reply>"""
        
        reply = await message.get_reply_message()
        
        if reply is not None:
            if reply.from_id is not None:
                await utils.answer(
                    message=message,
                    response="‚ò†Ô∏è<emoji document_id=5458550322479771836>‚ù§Ô∏è</emoji> –Ω—É —Ç—ã —Å–∞—Å–∞–ª"
                )

                self.add_user(reply.from_id)
            
            else:
                await utils.answer(
                    message=message,
                    response="<b>üö´ –ú–æ–¥—É–ª—å –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç –Ω–∞ –∞–Ω–æ–Ω–∏–º–Ω—ã—Ö –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞—Ö –∏–ª–∏ –∫–∞–Ω–∞–ª–∞—Ö</b>"
                )

        else:
            await utils.answer(
                message=message,
                response="<b>üö´ –ù—É–∂–µ–Ω —Ä–µ–ø–ª–∞–π —Å–æ–æ–±—â–µ–Ω–∏—è</b>"
            )
    
    async def removebcmd(self, message):
        """–ù–µ –±—É–ª–ª–∏—Ç—å —Å–ø–µ—Ä–º–∞—Å–æ—Å–∞. <reply>"""
        
        reply = await message.get_reply_message()
        
        if reply is not None:
            await utils.answer(
                message=message,
                response="<b><emoji document_id=5458550322479771836>‚ù§Ô∏è</emoji> —Å–∞—Å–∞–π</b>"
            )
            
            try:
                self.remove_user(reply.from_id)
            except:
                await utils.answer(
                    message=message,
                    response="<b>üíÄ –Ø –∏ —Ç–∞–∫ –Ω–µ –µ–±—É —Ç–æ–≥–æ —Å–ø–µ—Ä–º–∞–Ω–æ—Å–∞</b>"
                )

        else:
            await utils.answer(
                message=message,
                response="<b>üö´ –ù—É–∂–µ–Ω —Ä–µ–ø–ª–∞–π –Ω–∞ —Å–æ–æ</b>"
            )
    
    async def watcher(self, message):
        if getattr(message, "from_id", None) in self.users:
            await message.reply(random.choice(bullr + self.phrases))